    def eq(self):
        try:
            self.entrada = self.label.text()
            self.entrada = str(round(eval(self.entrada), 6))
            self.label.setText(self.entrada)
        except:
            self.label.clear()
            self.statusbar.showMessage("Error, Operacion invalida")

    def valor(self, valor):
        self.statusbar.showMessage("")
        self.entrada = self.label.text()
        self.entrada += valor
        self.label.setText(self.entrada)

    def back(self):
        self.entrada = self.label.text()
        self.entrada = self.entrada[:-1]
        self.label.setText(self.entrada)
        self.statusbar.showMessage("")

    def acerdaDe(self):
        msg = QMessageBox()
        msg.setWindowTitle("Acerca de")
        msg.setIcon(QMessageBox.Information)
        msg.setText("Desarrollado por Antonio Mejia")
        msg.setInformativeText("SEANT")
        msg.setStandardButtons(QMessageBox.Ok)
        msg.exec()

    def ayuda(self):
        msg = QMessageBox()
        msg.setIcon(QMessageBox.Question)
        msg.setWindowTitle('Ayuda')
        msg.setText("Estas es una calculadora de operaciones basica.")
        msg.setDetailedText("Mientras las operaciones sean validas se obtendran resultados, "
                            "en caso contrario, no se podra ejecutar la operacion y la pantalla"
                            " se limpiara automaticamente.")
        msg.setStandardButtons(QMessageBox.Ok)
        msg.exec()

    def clear(self):
        self.label.clear()
        self.statusbar.showMessage("Entrada Limpia")

    def mousePressEvent(self, event):
        if event.button() == QtCore.Qt.LeftButton:
            self.offset = event.pos()
        else:
            pass

    def mouseMoveEvent(self, event):
        if self.offset is not None and event.buttons() == QtCore.Qt.LeftButton:
            self.move(self.pos() + event.pos() - self.offset)
        else:
            pass

    def mouseReleaseEvent(self, event):
        self.offset = None
        pass

    def valor_pow(self):
        try:
            self.entrada = self.label.text()
            self.entrada = "(" + self.entrada + ")" + "*" + "(" + self.entrada + ")"
            self.entrada = str(round(eval(self.entrada), 6))
            self.label.setText(self.entrada)
        except:
            self.label.clear()
            self.statusbar.showMessage("Error, Operacion invalida")

    def raiz(self):
        try:
            self.entrada = self.label.text()
            self.entrada = str(round(math.sqrt(eval(self.entrada)), 6))
            self.label.setText(self.entrada)
        except:
            self.label.clear()
            self.statusbar.showMessage("Error, Operacion invalida")